[C#]
Excel excel = new Excel();
Validations validations = excel.Worksheets[0].Validations;
Validation validation = validations[validations.Add()];
validation.Type = Aspose.Excel.ValidationType.WholeNumber;
validation.Operator = OperatorType.Between;
validation.Formula1 = "3";
validation.Formula2 = "1234";

CellArea area;
area.StartRow = 0;
area.EndRow = 1;
area.StartColumn = 0;
area.EndColumn = 1;

validation.AreaList.Add(area);

[Visual Basic]
Dim excel as Excel = new Excel()
Validations validations = excel.Worksheets(0).Validations
Dim validation as Validation = validations(validations.Add())
validation.Type = ValidationType.WholeNumber
validation.Operator = OperatorType.Between
validation.Formula1 = "3"
validation.Formula2 = "1234"

Dim area as CellArea
area.StartRow = 0
area.EndRow = 1
area.StartColumn = 0
area.EndColumn = 1

validation.AreaList.Add(area)

